import React, { useState } from 'react';
import './c.css';

function ContadorP() {
    const [total, setTotal] = useState(0);
    const [homens, setHomens] = useState(0);
    const [mulheres, setMulheres] = useState(0);

    function adicionarHomem() {
        setHomens(homens + 1);
        setTotal(total + 1);
    }

    function removerHomem() {
        if (homens > 0) {
            setHomens(homens - 1);
            setTotal(total - 1);
        }
    }

    function adicionarMulher() {
        setMulheres(mulheres + 1);
        setTotal(total + 1);
    }

    function removerMulher() {
        if (mulheres > 0) {
            setMulheres(mulheres - 1);
            setTotal(total - 1);
        }
    }

    function resetClick() {
        setTotal(0);
        setHomens(0);
        setMulheres(0);
    }

    return (
        <div id="container">
            <div className="topo total"> 
                <h2>Total de Pessoas: {total}</h2>
            </div>
            <button className="reset-button" onClick={resetClick}>Reset</button>
            
            <div className="principal">
                <div className="contador-homens" style={{ gridArea: 'homens' }}>
                    <h3>Homens: {homens}</h3>
                    <button onClick={adicionarHomem}>Adicionar Homem</button>
                    <button onClick={removerHomem}>Remover Homem</button>
                </div>
                <div className="contador-mulheres" style={{ gridArea: 'mulheres' }}>
                    <h3>Mulheres: {mulheres}</h3>
                    <button onClick={adicionarMulher}>Adicionar Mulher</button>
                    <button onClick={removerMulher}>Remover Mulher</button>
                </div>
            </div>
        </div>
    );
}

export default ContadorP;
